import { getSceneParams } from "./getSceneParams";
import { useMemo } from 'react';
/**
 * 获取场景参数。
 *
 * @param parser 解析器
 */

export function useSceneParams(parser) {
  if (parser === void 0) {
    parser = 'searchParams';
  }

  return useMemo(function () {
    return getSceneParams(parser);
  }, []);
}