import { miniProgramBus } from "./miniProgramBus";
import { useEffect } from 'react';
import { useLatest } from "../react";
export function useShareAppMessage(callback) {
  var latestCallback = useLatest(callback);
  useEffect(function () {
    return miniProgramBus.on('currentPageShareAppMessage', function () {
      return latestCallback.current.apply(latestCallback, arguments);
    });
  }, []);
}